import React from 'react'
import Background from '../components/Background'
import Logo from '../components/Logo'
import Header from '../components/Header'
import Paragraph from '../components/Paragraph'
import Button from '../components/Button'
import { Animated, Dimensions, Image, Platform, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import 'react-native-gesture-handler';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

// Plus...
//import plus from './assets/plus.png'
import plus from './../assets/plus.png'

// Font Awesome Icons...
import { FontAwesome5 } from '@expo/vector-icons'
import { useRef } from 'react';


const Tab = createBottomTabNavigator();

// Hiding Tab Names...
export default function Dashboard() {
  // Animated Tab Indicator...
  const tabOffsetValue = useRef(new Animated.Value(0)).current;
  return (

   <NavigationContainer>


<Animated.View style={{
        width: getWidth() - 20,
        height: 2,
        backgroundColor: 'red',
        position: 'absolute',
        bottom: 98,
        // Horizontal Padding = 20...
        left: 50,
        borderRadius: 20,
        transform: [
          { translateX: tabOffsetValue }
        ]
      }}></Animated.View>

    </NavigationContainer>

  );
}

function getWidth() {
  let width = Dimensions.get("window").width

  // Horizontal Padding = 20...
  width = width - 80

  // Total five Tabs...
  return width / 5
}


function Tabs(){
  return(

    <Tab.Navigator>
    <Tab.Screen tabBarOptions={{
       showLabel: false,
       // Floating Tab Bar...
       style: {
         backgroundColor: 'white',
         position: 'absolute',
         bottom: 40,
         marginHorizontal: 20,
         // Max Height...
         height: 60,
         borderRadius: 10,
         // Shadow...
         shadowColor: '#000',
         shadowOpacity: 0.06,
         shadowOffset: {
           width: 10,
           height: 10
         },
         paddingHorizontal: 20,
       }
    }} name="Home" component={HomeScreen}
    options={{
      tabBarIcon: ({ focused }) => (
        <View style={{
          // centring Tab Button...
          position: 'absolute',
          top: 20
        }}>
          <FontAwesome5
            name="home"
            size={20}
            color={focused ? 'red' : 'gray'}
          ></FontAwesome5>
        </View>
      )
    }}


    listeners={({ navigation, route }) => ({
      // Onpress Update....
      tabPress: e => {
        Animated.spring(tabOffsetValue, {
          toValue: 0,
          useNativeDriver: true
        }).start();
      }
    })}


    />
    <Tab.Screen name="Settings" component={SettingsScreen}
    options={{
      tabBarIcon: ({ focused }) => (
        <View style={{
          // centring Tab Button...
          position: 'absolute',
          top: 20
        }}>
          <FontAwesome5
            name="search"
            size={20}
            color={focused ? 'red' : 'gray'}
          ></FontAwesome5>
        </View>
      )
    }}

    listeners={({ navigation, route }) => ({
      // Onpress Update....
      tabPress: e => {
        Animated.spring(tabOffsetValue, {
          toValue: getWidth(),
          useNativeDriver: true
        }).start();
      }
    })}


    />

<Tab.Screen name="car" component={NotificationScreen}
options={{
  tabBarIcon: ({ focused }) => (

    <TouchableOpacity>
      <View style={{
        width: 55,
        height: 55,
        backgroundColor: 'red',
        borderRadius: 30,
        justifyContent: 'center',
        alignItems: 'center',
        marginBottom: Platform.OS == "android" ? 50 : 30
      }}>
        <Image source={plus} style={{
          width: 22,
          height: 22,
          tintColor: 'white',
        }}></Image>
      </View>
    </TouchableOpacity>
  )
}}

/>
<Tab.Screen name="caw" component={EmptyScreen} 

options={{
  tabBarIcon: ({ focused }) => (
    <View style={{
      // centring Tab Button...
      position: 'absolute',
      top: 20
    }}>
      <FontAwesome5
        name="bell"
        size={20}
        color={focused ? 'red' : 'gray'}
      ></FontAwesome5>
    </View>
  )
}} listeners={({ navigation, route }) => ({
  // Onpress Update....
  tabPress: e => {
    Animated.spring(tabOffsetValue, {
      toValue: getWidth() * 3,
      useNativeDriver: true
    }).start();
  }
})}


/>
<Tab.Screen name="cate" component={SearchScreen}

options={{
  tabBarIcon: ({ focused }) => (
    <View style={{
      // centring Tab Button...
      position: 'absolute',
      top: 20
    }}>
      <FontAwesome5
        name="user-alt"
        size={20}
        color={focused ? 'red' : 'gray'}
      ></FontAwesome5>
    </View>
  )
}} listeners={({ navigation, route }) => ({
  // Onpress Update....
  tabPress: e => {
    Animated.spring(tabOffsetValue, {
      toValue: getWidth() * 4,
      useNativeDriver: true
    }).start();
  }
})}


/>
  </Tab.Navigator>
  );
}

function EmptyScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
    </View>
  );
}

function SettingsScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Settings!</Text>
    </View>
  );
}

function HomeScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Home!</Text>
    </View>
  );
}

function NotificationScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Notifications!</Text>
    </View>
  );
}

function SearchScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Search!</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
